    public function feedback ($text, $style, $extra = '') {
        die($extra.' App.feedBack("'.$style.'","'.$text.'")');
    }

    protected function saveAction() {
        if (!$this->reCaptcha($this->getParam('g-recaptcha-response'))) {
            $this->feedback(__('reCaptcha'),"danger");
        }
        $globalEmail = $this->getConfig();

        $form = $this->getParam('form');

        $checkbox = $this->getParam('objectCheck');
        $checkboxAll = $this->getParam('objectCheckAll');
        $inputs = $this->getParam('object');

        $w = array();

        if($checkboxAll == '') {
            $this->feedback(__('parameters_not_set'),"danger");
        }
        if($checkbox == '') {
            $this->feedback(__('selected_input_empty'),"danger");
        }
        foreach ($checkbox as $k => $v) {
            $rt = $this->selectPathArray($k,$checkboxAll);
            if($rt != false) {
                $w[$k] = $rt;
            }
        }
        if(empty($w)) {
            $this->feedback(__('selected_input_empty'),"danger");
        }
        $save = array();
        $dataVal = '<ul>';
        foreach ($w as $key => $value) {
            $dataVal .= '<li>';
            $keyValue = (preg_grep('/^.*' . $key . '.*/', array_keys($inputs)));
            $dataVal .= str_replace($key.' &#8594;', '', $value);

            foreach ($keyValue as $v) {
                $save[$v] = $inputs[$v];
                $dataVal .= '<span style="color: black; font-weight: bold">'.$inputs[$v].'<span>';
                if ($v != end($keyValue)) $dataVal .= ' ';
            }
            $dataVal .= '</li>';
        }
        $dataVal .= '</ul>';

        if (isset($form['other_activity']) && ($form['activity'] == __('other'))) {
            $form['activity']=$form['other_activity'];
        }
        unset($form['other_activity']);

        $array = array_merge($form,$save);
        unset($array['recaptcha_response_field']);

        $this->_model->insert('ankieta',$array);

        $message = $this->message_head;
        $message .= sprintf($this->message_body, $form['company'], $form['first_name'], $form['last_name'], $form['email'], $form['activity'],$dataVal);
        $message .= $this->message_foot;

        $mail = $this->mailClass();
        $mail->setFrom($globalEmail['email.Username'],$globalEmail['email.User']);
        $mail->addAddress($globalEmail['email.Address'],$globalEmail['email.Name']);
        $mail->isHTML(true);
        $mail->Subject  = 'Ankieta';
        $mail->Body     =  $message;
        $mail->IsHTML(true);
        if(!$mail->send()) {
            $this->feedback(__('not_send_mail'),"danger");
        } else {
            $extra = ' App.clearForm(); ';
            $this->feedback(__('send_mail'),"success", $extra);
        }
    }

    private function reCaptcha($response) {

        $resp = recaptcha_check_answer ($this->_privateKey,
            $_SERVER["REMOTE_ADDR"],
            $_POST["recaptcha_challenge_field"],
            $_POST["recaptcha_response_field"]);

        if (!$resp->is_valid) {
            // What happens when the CAPTCHA was entered incorrectly
            return false;
        } else {
            return true;
            // Your code here to handle a successful verification
        }

    }

    public function getPathFromArray($toBeSearchedArray, $searchValue, &$exactPath, &$ca = array())
    {
        foreach ($toBeSearchedArray as $key => $value) {
            if (count($value) > 0 && is_array($value)) {
                $found = $this->getPathFromArray($value, $searchValue, $exactPath,$ca);
                if ($found) {
                    if (isset($value['name'])) {
                         $exactPath = '<span style="color:#005AAB">'.__($value['name'],true) . " &#8594; " . $exactPath .'<span>';
                    }
                    if(isset($value['input'])){
                        foreach ($value['input'] as $input) {
                            if($input['type'] == 'checkbox') {
                                $ca[] = $input['name'];
                            }
                        }
                    }
                    return TRUE;
                }
            }
            if ($value == $searchValue) {
                return true;
            }
        }
        return false;
    }
    protected function selectPathArray($k, $checbox) {
        $exactPath = '';
        $string = $k;
        $path = NULL;

        $it = new RecursiveIteratorIterator(
            new RecursiveArrayIterator(json_decode($this->_json,true)), RecursiveIteratorIterator::SELF_FIRST
        );
        foreach ($it as $key => $value) {

            if ($value !== $string) {
                continue;
            }
            $path = array($string);
            for ($count = $it->getDepth(); $count && $count--;) {

                array_unshift($path, $it->getSubIterator($count)->key());
            }
            break;
        }
        $array = array_splice(json_decode($this->_json,true), $path[0]);
        $this->getPathFromArray($array, $string, $exactPath, $ca);
        $t = true;
        foreach ($ca as $c) {
            if(!array_key_exists($c,$checbox)) $t = false;
        }
        if($t == false) return false;
        return $exactPath;
    }
